mapper.select:
select name,hour,
${caseblock}
from (
select name,hour,app_name,count(*) stat
from (

select * from (
(select tmp.*,u.name,app.name app_name
,date_format( str_to_date(action_date,'%Y-%m-%d %H:%i:%s'),'${dateFormat}') hour
from user_action_log tmp
left join user_action_log_template app on tmp.type_first=app.type_first
left join user u on tmp.userid=u.userid
where tmp.type_first in('应用服务','办事指南')

	and u.sex = ${sex}


	and u.age > ${age1} and u.age < ${age2}

)
union
(
select tmp.*,u.name,app.name app_name
,date_format( str_to_date(action_date,'%Y-%m-%d %H:%i:%s'),'${dateFormat}') hour
from user_action_log tmp
left join user_action_log_template app on tmp.type_second=app.type_second
and tmp.type_first=app.type_first
left join user u on tmp.userid=u.userid
where tmp.type_first not in('应用服务','办事指南','svr_error')

	and u.sex = ${sex}


	and u.age > ${age1} and u.age < ${age2}

)
) innertmp where 1=1
and str_to_date(action_date,'${dateFormat}') BETWEEN
str_to_date('${time1}','${dateFormat}')
and str_to_date('${time2}','${dateFormat}')
and name like '%${name}%'


) tmp2 group by hour,name,app_name
) tmp3 group by name,hour
limit ${start},${pageCount}

select name,hour,
${caseblock}
from (
select name,hour,app_name,count(*) stat
from (
select tmp.*,u.name,app.app_name
,date_format( str_to_date(action_date,'%Y-%m-%d %H:%i:%s'),'${dateFormat}') hour
from user_action_log tmp
left join app_service_info app on tmp.type_second=app.app_id
left join user u on tmp.userid=u.userid
where tmp.type_first='应用服务'
and
str_to_date(action_date,'${dateFormat}') BETWEEN str_to_date('${time1}','${dateFormat}')
and str_to_date('${time2}','${dateFormat}')
and u.name like '%${name}%'


	and u.sex = ${sex}


	and u.age > ${age1} and u.age < ${age2}



order by str_to_date(action_date,'%Y-%m-%d %H %i %s'),tmp.userid desc
) tmp2 group by hour,name,app_name
) tmp3 group by name,hour
limit ${start},${pageCount}

select name,userid, ${caseblock} from ( select u.name,u.userid,stat,app.app_name,app.app_id from (
select u.userid,name,account_code,'不关注' stat from user u left join pp_account_user_never_focus n on u.userid=n.userid where account_code is not null union
select * FROM( select u.userid,name,app_id account_code,'关注' stat from user u join app_service_info n ) tmp where not exists( select * from user u left join pp_account_user_never_focus n on u.userid=n.userid where account_code is not null and u.userid=tmp.userid and account_code=tmp.account_code ) ) tmp2 left join app_service_info app on tmp2.account_code=app.app_id left join user u on tmp2.userid=u.userid
where app.app_name is not null and u.name like CONCAT('%','${name}','%')
and u.sex = ${sex}
and u.age > ${age1} and u.age < ${age2}
and app.app_id in ( ${inblock} ) ) tmp3 group by userid limit ${start},${pageCount}
