mapper.sql:
( select c.ID, c.VERSION_ID, c.CLS_NAME,c.PROPERTIES, c.CODE, c. PARENT_ID, c.SORT, c. DIR_ID, c.DIR_CODE, c.ENABLED, c.DELETED, c.CREATED_USER, c.CREATED_TIME, c.remark, c.UPDATED_USER, c.UPDATED_TIME from KG_CLASS c
UNION all ( select c.ID, c.VERSION_ID, c.CLS_NAME,c.PROPERTIES, c.CODE, c. PARENT_ID, c.SORT, c. DIR_ID, c.DIR_CODE, c.ENABLED, c.DELETED, c.CREATED_USER, c.CREATED_TIME, c.remark, c.UPDATED_USER, c.UPDATED_TIME from KG_EDIT_CLASS c left join kg_edit_data e on c.id=e.edit_data_id where 1=1 and e.edit_user=#{curUser} and e.edit_action='1' and e.audit_state in ('1','2') )
)

( select c.ID, c.VERSION_ID, c.NAME, c.REMARK, c.CREATED_USER, c.CREATED_TIME, c.UPDATED_USER, c.UPDATED_TIME ,c. DELETED,c. ENABLED from KG_SUB_GROUP c
union ( select c.ID, c.VERSION_ID, c.NAME, c.REMARK, c.CREATED_USER, c.CREATED_TIME, c.UPDATED_USER, c.UPDATED_TIME ,'0' DELETED,'1' ENABLED from KG_EDIT_SUB_GROUP c left join kg_edit_data e on c.id=e.edit_data_id where 1=1 and e.edit_user=#{curUser} and e.edit_action='1' and e.audit_state in ('1','2') )
)

( select c.ID, c.VERSION_ID, c.NAME, c.DATA_TYPE_RULE, c.EQUIVALENCE, c.REMARK, c.ENABLED, c.DELETED, c.CREATED_USER, c.CREATED_TIME, c.UPDATED_USER, c.UPDATED_TIME from KG_PROPERTY c
union ( select c.ID, c.VERSION_ID, c.NAME, c.DATA_TYPE_RULE, c.EQUIVALENCE, c.REMARK, c.ENABLED, c.DELETED, c.CREATED_USER, c.CREATED_TIME, c.UPDATED_USER, c.UPDATED_TIME from KG_EDIT_PROPERTY c left join kg_edit_data e on c.id=e.edit_data_id where 1=1 and e.edit_user=#{curUser} and e.edit_action='1' and e.audit_state in ('1','2') )
)

mapper.select:
select c.*,tp.nodenum from c left join ( select distinct c.id as id, count(c2.id) nodenum ,GROUP_CONCAT(c2.cls_name) nodenames from c join c2 on c2.parent_id=c.id where c.deleted='0' and c2.deleted='0' group by c.id
) tp on c.id=tp.id left join ( select c.id ,GROUP_CONCAT(kp.name order by kp.id) subGroupNames,GROUP_CONCAT(kp.id order by kp.id) subGroupIds from c left join KG_OBJECT_SUB_GROUP op on c.id=op.obj_id left join kp on op.sub_group_id=kp.id group by c.id ) g on c.id=g.id where 1=1 and c.deleted='0' and c.PARENT_ID =#{id}
AND ( g.subGroupIds like #{dirId} or g.subGroupIds is null )
order by c.sort asc
