mapper.select:
select t.*,r3.userid tuid,r3.user_blog tuser_blog from ( select r.*, case when r2.replay_recordid =0 then r2.recordid when r2.replay_recordid is null then 0 else r2.replay_recordid end o_ppid from replay_info r LEFT JOIN replay_info r2 on r.replay_recordid=r2.recordid ) t left join replay_info r3 on t.replay_recordid=r3.recordid
where 1=1 and t.isvalid="0"
and t.blogimei =#{blogimei}
and t.state =#{state}
order by t.create_date desc



    select count(*)  from replay_info t
    where 1=1 and isvalid="0" and state="1"

			and t.blogimei =#{blogimei}


    select *  from replay_info t
    where 1=1

			and t.recordid =#{recordid}



			and t.blogimei =#{blogimei}

mapper.insert:



update replay_info
set  state=#{state}
where recordid=#{recordid}
