mapper.select:
select substring_index(substring_index(cp.ipdesc, '-',1), '.',3) ippre , cast( substring_index(substring_index(cp.ipdesc, '-',1), '.',-1) as SIGNED INTEGER) ipstart ,cast( substring_index(substring_index(cp.ipdesc, '-',-1), '.',-1) as SIGNED INTEGER) ipend , cp.* from company_proxyserver cp where substring_index(substring_index(cp.ipdesc, '-',1), '.',3) =substring_index(substring_index(#{ipdesc}, '-',1), '.',3) and
( (
cast( substring_index(substring_index(#{ipdesc}, '-',1), '.',-1) as SIGNED INTEGER) >=cast( substring_index(substring_index(cp.ipdesc, '-',1), '.',-1) as SIGNED INTEGER) and
cast( substring_index(substring_index(#{ipdesc}, '-',1), '.',-1) as SIGNED INTEGER) <=cast( substring_index(substring_index(cp.ipdesc, '-',-1), '.',-1) as SIGNED INTEGER) ) or ( cast( substring_index(substring_index(#{ipdesc}, '-',-1), '.',-1) as SIGNED INTEGER) >=cast( substring_index(substring_index(cp.ipdesc, '-',1), '.',-1) as SIGNED INTEGER) and cast( substring_index(substring_index(#{ipdesc}, '-',-1), '.',-1) as SIGNED INTEGER) <=cast( substring_index(substring_index(cp.ipdesc, '-',-1), '.',-1) as SIGNED INTEGER) ) )
and proxyserver_id=#{proxyserver_id}
and recordid !=#{recordid}

		select *  from company_proxyserver a  left join proxyserver_info p on a.proxyserver_id=p.id
		where 1=1
		
			and a.proxyserver_id = #{proxyserver_id}
		
		
			and a.companyid = #{companyid} 
		
		

		limit #{start},#{pageCount}

select count(*) from company_proxyserver a 
where 1=1
	
	and a.proxyserver_id = #{proxyserver_id}


	and a.companyid = #{companyid}
